import TextField from './TextField';
import FormField from '../prototypes/FormField';
import { useEffect, useState } from 'react';
import {getFormData, getFormRules} from './../apis/request';
import {fieldDataMapGenerator, ruleMapGenerator} from './../util/dataInterpreter';
import RenderFormFields from './RenderFormFields';

export default function FormContainer() {
  const [formData, setFormData] = useState([]);
  const [formDataMap, setFormDataMap] = useState([]);
  const [formRulesMap, setFormRulesMap] = useState([]);

  useEffect(()=>{
    const data = getFormData();
    const formRules = getFormRules();
    setFormData(data);
    setFormDataMap(fieldDataMapGenerator(data));
    setFormRulesMap(ruleMapGenerator(formRules));
  }, []);

  return (
    <div className="row">
      <div className="col-sm-12">
        <form className="needs-validation">
          <div className="form-row">
            <RenderFormFields {...{formData}}/>;
          </div>
          <button className="btn btn-primary" type="submit">Submit form</button>
        </form>
      </div>
    </div>
  );
}
