import TextField from './TextField';
import FormField from '../prototypes/FormField';
import { useEffect, useState } from 'react';
import {getFormData} from './../apis/request';

export default function FormContainer() {
  const [data, setFormData] = useState([]);

  useEffect(()=>{
    setFormData(getFormData());
  }, []);

  return (
    <div className="row">
      <div className="col-sm-12">
        <form className="needs-validation">
          <div className="form-row">
            {renderFormFields(data)}
          </div>
          <button className="btn btn-primary" type="submit">Submit form</button>
        </form>
      </div>
    </div>
  );
}

function renderFormFields(FORMDATA){
  return (
    <>
      {
        FORMDATA.map((data, index) => {
          if((data.value.__proto__.constructor.name === "Array") && data.data_type === "group"){
            return renderFormFields(data.value);
          }else{
            const {uid, label, type, defaultValue, metadata} = new FormField(data);
            return (<TextField
              key={`${uid}_${index}`}
              {...{uid, label, type, defaultValue}}
              {...metadata}
            />)
          }
        })
      }
    </>
  )
}
