import { useState } from "react";

export default function FormContainer() {

  const DATA = [
    {
      "uid": "name",
      "data_type": "string",
      "label": "First Name",
      "value": "",
      "_metadata": {
        "required": true
      }
    },
    {
      "uid": "name1",
      "data_type": "string",
      "label": "Last Name",
      "value": "harshal",
      "_metadata": {
        "required": true
      }
    },
    {
      "uid": "age",
      "data_type": "number",
      "value": "28"
    }
  ]

  return (
    <div className="row">
      <div className="col-sm-12">
        <form className="needs-validation">
          <div className="form-row">
            {
              DATA.map((data)=>{
                const {uid, label, type, defaultValue, metadata} = new FormFieldProps(data);

                return(
                  <TextField
                    key={uid}
                    {...{uid, label, type, defaultValue}}
                    {...metadata}
                  />
                )
              })
            }
          </div>
          <button className="btn btn-primary" type="submit">Submit form</button>
        </form>
      </div>
    </div>
  );
}

function FormFieldProps(data){
  this.uid = data.uid;
  this.label = data.label;
  this.type = data.data_type;
  this.defaultValue = data.value;
  this.metadata = data._metadata;
  if(this.metadata.required){
    this.label = `${data.label}*`;
    this.metadata["errorMessage"] = `${data.label} is required field!`;
  }
}

function TextField(props){
  const {
    uid,
    type = "string",
    label,
    errorMessage,
    required=false,
    onChange
  } = props;

  const [value, setValue] = useState(props.defaultValue || props.value || "");
  const [isValid, checkValidation] = useState(true);

  let fieldType = "text";

  switch(type){
    case "number":
      fieldType = "number";
      break;
    case "string":
    case "text":
    default:
      fieldType = "text";
      break;
  }

  const onChangeHandler=(e)=>{
    const value = e.target.value;
    setValue(value);
    onChange && onChange(value);
    checkValidation(!!value);
  }

  const onBlurHandler=(e)=>{
    const value = e.target.value;
    checkValidation(!!value);
  }

  return(
    <div className="col-sm-12 mb-3">
      <label htmlFor={uid}>{label}</label>
      <input
        id={uid}
        className="form-control"
        type={fieldType} 
        placeholder={label}
        value={value}
        required={!!required}
        onChange={onChangeHandler}
        onBlur={onBlurHandler}
      />
      {errorMessage && <div className="invalid-feedback" style={!isValid ? {"display": "block"} : {}}>
        {errorMessage}
      </div>}
    </div>
  )
}
