export default function ruleEngine(fieldUID, formData, formRule){
  const formFieldDetail = formRule[fieldUID];
  const {operations, withValue, path, opernadFieldUid} = formFieldDetail;
  const targetValue = getValueFromPath(path, formData);
  console.log({operations, withValue, targetValue, opernadFieldUid});



  return true;
}

function getValueFromPath(path, sourceData){
  const pathMap = Array.isArray(path) ? path : path.split(".");
  let value = sourceData;
  pathMap.forEach(key=>{
    value = value[key];
  })
  return value;
}
